import React, { useEffect, useState } from "react"; 
import { MapContainer, TileLayer, Marker, Popup, LayersControl, Polygon } from "react-leaflet";
import tileLayer from "../util/tileLayer";
import "./MapForm.css"; // CSS dosyasını import ettik

const { BaseLayer, Overlay } = LayersControl;

const center = [39.9334, 32.8597]; // Ankara koordinatları
const defaultZoom = 6;

const MapWrapper = () => {
  const [map, setMap] = useState(null);
  const [intersections, setIntersections] = useState([]);
  const [selectedIntersection, setSelectedIntersection] = useState(null);
  const [formData, setFormData] = useState({
    name: "",
    type: "",
    city: "",
    latitude: "",
    longitude: "",
    mahalCode: "",
    fee: "",
    responsible: ""
  });

  useEffect(() => {
    // Tarayıcının önbelleğinden verileri getir
    const cachedIntersections = JSON.parse(localStorage.getItem("intersections"));
    if (cachedIntersections) {
      setIntersections(cachedIntersections);
    }
  }, []);

  useEffect(() => {
    // Verileri tarayıcının önbelleğine kaydet
    localStorage.setItem("intersections", JSON.stringify(intersections));
  }, [intersections]);

  const handleViewportChanged = (viewport) => {
    const bounds = map.getBounds();
    const NE = bounds.getNorthEast();
    const SW = bounds.getSouthWest();

    // Burada haritada görünen bölgenin koordinatlarını kullanarak
    // bu bölge için verileri getirebilir ve setIntersections ile güncelleyebilirsiniz
    console.log("Kuzeydoğu Koordinatları:", NE);
    console.log("Güneybatı Koordinatları:", SW);
  };

  const handleMarkerClick = (intersection) => {
    setSelectedIntersection(intersection);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const newIntersection = { ...formData };
    setIntersections([...intersections, newIntersection]);
    setFormData({
      name: "",
      type: "",
      city: "",
      latitude: "",
      longitude: "",
      mahalCode: "",
      fee: "",
      responsible: ""
    });
  };

  const createSquareBounds = (lat, lng) => {
    const halfSideLength = 0.0025; // Kare kenar uzunluğunun yarısı
    return [
      [lat + halfSideLength, lng - halfSideLength], // Sol üst köşe
      [lat + halfSideLength, lng + halfSideLength], // Sağ üst köşe
      [lat - halfSideLength, lng + halfSideLength], // Sağ alt köşe
      [lat - halfSideLength, lng - halfSideLength], // Sol alt köşe
    ];
  };

  const handleCityChange = (e) => {
    const city = e.target.value;
    // Seçilen şehre göre enlem ve boylamı güncelle
    if (city === "Ankara") {
      setFormData({
        ...formData,
        city: "Ankara",
        latitude: "39.9334",
        longitude: "32.8597"
      });
    } else if (city === "İstanbul") {
      setFormData({
        ...formData,
        city: "İstanbul",
        latitude: "41.0082",
        longitude: "28.9784"
      });
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  return (
    <div className="map-container">
      <form className="intersection-form" onSubmit={handleSubmit}>
        <h2>Mahal Ekle</h2>
        <label>
          Mahal Adı:
          <input type="text" name="name" value={formData.name} onChange={handleInputChange} />
        </label>
        <label>
          Mahal Tipi:
          <select name="type" value={formData.type} onChange={handleInputChange}>
            <option value="">Seçiniz</option>
            <option value="Mahal">Mahal</option>
            <option value="Diğer">Diğer</option>
          </select>
        </label>
        <label>
          Şehir:
          <select name="city" onChange={handleCityChange}>
            <option value="">Şehir Seçiniz</option>
            <option value="Ankara">Ankara</option>
            <option value="İstanbul">İstanbul</option>
          </select>
        </label>
        <label>
          Latitude:
          <input type="text" name="latitude" value={formData.latitude} onChange={handleInputChange} />
        </label>
        <label>
          Longitude:
          <input type="text" name="longitude" value={formData.longitude} onChange={handleInputChange} />
        </label>
        <label>
          Mahal Kodu:
          <input type="text" name="mahalCode" value={formData.mahalCode} onChange={handleInputChange} />
        </label>
        <label>
          Ücret:
          <input type="text" name="fee" value={formData.fee} onChange={handleInputChange} />
        </label>
        <label>
          Sorumlu:
          <input type="text" name="responsible" value={formData.responsible} onChange={handleInputChange} />
        </label>
        <button type="submit">Mahal Ekle</button>
      </form>

      <MapContainer
        whenCreated={setMap}
        center={[parseFloat(formData.latitude), parseFloat(formData.longitude)]}
        zoom={defaultZoom}
        scrollWheelZoom={true} 
        onViewportChanged={handleViewportChanged}
      >
        <LayersControl position="topright">
          <BaseLayer checked name="OpenStreetMap">
            <TileLayer {...tileLayer} />
          </BaseLayer>

          <Overlay checked name="Mahaller">
            {intersections.map((intersection, index) => (
              <Marker
                key={index}
                position={[parseFloat(intersection.latitude), parseFloat(intersection.longitude)]}
                eventHandlers={{
                  click: () => handleMarkerClick(intersection),
                }}
              >
                <Popup>
                  <div className="popup-content">
                    <p><strong>Mahal Adı:</strong> {intersection.name}</p>
                    <p><strong>Mahal Tipi:</strong> {intersection.type}</p>
                    <p><strong>Şehir:</strong> {intersection.city}</p>
                    <p><strong>Latitude:</strong> {intersection.latitude}</p>
                    <p><strong>Longitude:</strong> {intersection.longitude}</p>
                    <p><strong>Mahal Kodu:</strong> {intersection.mahalCode}</p>
                    <p><strong>Ücret:</strong> {intersection.fee}</p>
                    <p><strong>Sorumlu:</strong> {intersection.responsible}</p>
                    {/* Diğer bilgiler buraya eklenebilir */}
                  </div>
                </Popup>
              </Marker>
            ))}
          </Overlay>
        </LayersControl>

        {selectedIntersection && (
          <Popup position={[parseFloat(selectedIntersection.latitude), parseFloat(selectedIntersection.longitude)]} onClose={() => setSelectedIntersection(null)}>
            <div>
              <h3>{selectedIntersection.name}</h3>
              <p><strong>Mahal Tipi:</strong> {selectedIntersection.type}</p>
              <p><strong>Şehir:</strong> {selectedIntersection.city}</p>
              <p><strong>Latitude:</strong> {selectedIntersection.latitude}</p>
              <p><strong>Longitude:</strong> {selectedIntersection.longitude}</p>
              <p><strong>Mahal Kodu:</strong> {selectedIntersection.mahalCode}</p>
              <p><strong>Ücret:</strong> {selectedIntersection.fee}</p>
              <p><strong>Sorumlu:</strong> {selectedIntersection.responsible}</p>
              {/* Diğer bilgiler buraya eklenebilir */}
            </div>
          </Popup>
        )}

        {/* Kare içindeki noktaları çizmek için marker kullanımı */}
        {intersections.map((intersection, index) => (
          <Polygon
            key={index}
            pathOptions={{ color: "blue" }}
            positions={createSquareBounds(parseFloat(intersection.latitude), parseFloat(intersection.longitude))}
          />
        ))}
      </MapContainer>
    </div>
  );
};

export default MapWrapper;
