import React, { useState, useEffect } from "react"; 
import { MapContainer, TileLayer, Marker, Popup, LayersControl, Polygon } from "react-leaflet";
import tileLayer from "../util/tileLayer";
import "./MapForm.css"; // CSS dosyasını import ettik

const { BaseLayer, Overlay } = LayersControl;

const MapWrapper = () => {
  const [map, setMap] = useState(null);
  const [mahalleler, setMahalleler] = useState([]);
  const [selectedMahalle, setSelectedMahalle] = useState(null);
  const [formData, setFormData] = useState({
    mahalleAdi: "",
    mahalleKodu: "",
    aylıkKira: "",
    ggkpOran: "",
    sorumlular: ""
  });

  useEffect(() => {
    // Tarayıcının önbelleğinden verileri getir
    const cachedMahalleler = JSON.parse(localStorage.getItem("mahalleler"));
    if (cachedMahalleler) {
      setMahalleler(cachedMahalleler);
    }
  }, []);

  useEffect(() => {
    // Verileri tarayıcının önbelleğine kaydet
    localStorage.setItem("mahalleler", JSON.stringify(mahalleler));
  }, [mahalleler]);

  const handleViewportChanged = (viewport) => {
    // Harita bölgesi değiştiğinde burada işlemler yapabilirsiniz
  };

  const handleMarkerClick = (mahalle) => {
    setSelectedMahalle(mahalle);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const newMahalle = { ...formData };
    setMahalleler([...mahalleler, newMahalle]);
    setFormData({
      mahalleAdi: "",
      mahalleKodu: "",
      aylıkKira: "",
      ggkpOran: "",
      sorumlular: ""
    });
  };

  const createSquareBounds = (lat, lng) => {
    const halfSideLength = 0.0025; // Kare kenar uzunluğunun yarısı
    return [
      [lat + halfSideLength, lng - halfSideLength], // Sol üst köşe
      [lat + halfSideLength, lng + halfSideLength], // Sağ üst köşe
      [lat - halfSideLength, lng + halfSideLength], // Sağ alt köşe
      [lat - halfSideLength, lng - halfSideLength], // Sol alt köşe
    ];
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  return (
    <div className="map-container">
      <form className="mahalle-form" onSubmit={handleSubmit}>
        <h2>Mahalle Ekle</h2>
        <label>
          Mahalle Adı:
          <input type="text" name="mahalleAdi" value={formData.mahalleAdi} onChange={handleInputChange} required />
        </label>
        <label>
          Mahalle Kodu:
          <input type="text" name="mahalleKodu" value={formData.mahalleKodu} onChange={handleInputChange} required />
        </label>
        <label>
          Aylık Kira:
          <input type="text" name="aylıkKira" value={formData.aylıkKira} onChange={handleInputChange} required />
        </label>
        <label>
          GGKP Oranı:
          <input type="text" name="ggkpOran" value={formData.ggkpOran} onChange={handleInputChange} required />
        </label>
        <label>
          Sorumlular:
          <input type="text" name="sorumlular" value={formData.sorumlular} onChange={handleInputChange} required />
        </label>
        <button type="submit">Mahalle Ekle</button>
      </form>

      <MapContainer
        whenCreated={setMap}
        center={[39.9334, 32.8597]} // Ankara koordinatları
        zoom={6}
        scrollWheelZoom={true} 
        onViewportChanged={handleViewportChanged}
      >
        <LayersControl position="topright">
          <BaseLayer checked name="OpenStreetMap">
            <TileLayer {...tileLayer} />
          </BaseLayer>

          <Overlay checked name="Mahalleler">
            {mahalleler.map((mahalle, index) => (
              <Marker
                key={index}
                position={[parseFloat(mahalle.latitude), parseFloat(mahalle.longitude)]}
                eventHandlers={{
                  click: () => handleMarkerClick(mahalle),
                }}
              >
                <Popup>
                  <div className="popup-content">
                    <p><strong>Mahalle Adı:</strong> {mahalle.mahalleAdi}</p>
                    <p><strong>Mahalle Kodu:</strong> {mahalle.mahalleKodu}</p>
                    <p><strong>Aylık Kira:</strong> {mahalle.aylıkKira}</p>
                    <p><strong>GGKP Oranı:</strong> {mahalle.ggkpOran}</p>
                    <p><strong>Sorumlular:</strong> {mahalle.sorumlular}</p>
                    {/* Diğer bilgiler buraya eklenebilir */}
                  </div>
                </Popup>
              </Marker>
            ))}
          </Overlay>
        </LayersControl>

        {selectedMahalle && (
          <Popup position={[selectedMahalle.latitude, selectedMahalle.longitude]} onClose={() => setSelectedMahalle(null)}>
            <div>
              <h3>{selectedMahalle.mahalleAdi}</h3>
              <p><strong>Mahalle Kodu:</strong> {selectedMahalle.mahalleKodu}</p>
              <p><strong>Aylık Kira:</strong> {selectedMahalle.aylıkKira}</p>
              <p><strong>GGKP Oranı:</strong> {selectedMahalle.ggkpOran}</p>
              <p><strong>Sorumlular:</strong> {selectedMahalle.sorumlular}</p>
              {/* Diğer bilgiler buraya eklenebilir */}
            </div>
          </Popup>
        )}

        {/* Kare içindeki noktaları çizmek için polygon kullanımı */}
        {mahalleler.map((mahalle, index) => (
          <Polygon
            key={index}
            pathOptions={{ color: "blue" }}
            positions={createSquareBounds(parseFloat(mahalle.latitude), parseFloat(mahalle.longitude))}
          />
        ))}
      </MapContainer>
    </div>
  );
};

export default MapWrapper;
