import { useEffect, useState } from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import L from "leaflet";
import tileLayer from "../util/tileLayer";
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css';

const center = [52.242, 21.017532];

const colors = ["fe4848", "fe6c58", "fe9068", "feb478", "fed686"];
const labels = ["2-12.5", "12.6-16.8", "16.9-20.9", "21-25.9", "26-plus"];

const points = [
  {
    lat: 52.228956,
    lng: 21.003799,
    title: "Atatürk Havalimanı",
    propertyDetails: {
      floors: 5,
      numberOfApartment: 10,
      status: "Kiralanmış",
      responsible: "Ahmet Bey",
      projectDetails: {
        completionDate: "2022-05-30",
        budget: "10.000.000 TL",
        architect: "Mehmet Bey",
      },
    },
    images: [
      "./images/1.jpeg",
      "./images/2.jpeg",
      "./images/3.jpeg", 
    ]
  },
  {
    lat: 52.258071,
    lng: 20.986805,
    title: "Konya Havalimanı",
    propertyDetails: {
      floors: 8,
      numberOfApartment: 16,
      status: "Satın Alınmış",
      responsible: "Ayşe Hanım",
      projectDetails: {
        completionDate: "2023-08-15",
        budget: "15.000.000 TL",
        architect: "Fatma Hanım",
      },
    },
  },
  {
    lat: 52.242728,
    lng: 21.041565,
    title: "Zafer Havalimanı",
    propertyDetails: {
      floors: 10,
      numberOfApartment: 20,
      status: "Kiralanmış",
      responsible: "Mehmet Bey",
      projectDetails: {
        completionDate: "2022-12-10",
        budget: "12.500.000 TL",
        architect: "Ayşe Hanım",
      },
    },
    images: [
      "./images/1.jpeg",
      "./images/2.jpeg",
      "./images/3.jpeg", 
    ]
  },
  {
    lat: 52.234213,
    lng: 21.029034,
    title: "Kars Havalimanı",
    propertyDetails: {
      floors: 3,
      numberOfApartment: 5,
      status: "Satın Alınmış",
      responsible: "Fatma Hanım",
      projectDetails: {
        completionDate: "2024-02-20",
        budget: "8.000.000 TL",
        architect: "Ahmet Bey",
      },
    },
    images: [
      "https://placehold.co/800x400",
      "https://placehold.co/600x400",
      "https://placehold.co/600x400",
      "https://placehold.co/600x400"
    ]
  },
  {
    lat: 52.251661,
    lng: 21.003456,
    title: "İzmir Havalimanı",
    propertyDetails: {
      floors: 6,
      numberOfApartment: 7,
      status: "Kiralanmış",
      responsible: "Ayşe Hanım",
      projectDetails: {
        completionDate: "2023-06-25",
        budget: "11.000.000 TL",
        architect: "Mehmet Bey",
      },
    },
    images: [
      "https://placehold.co/800x400",
      "https://placehold.co/600x400",
      "https://placehold.co/600x400",
      "https://placehold.co/600x400"
    ]
  },
];
const Legend = ({ map }) => {
  useEffect(() => {
    if (!map) return;

    const legend = L.control({ position: "bottomright" });

    const rows = [];
    legend.onAdd = () => {
      const div = L.DomUtil.create("div", "legend");
      colors.map((color, index) => {
        return rows.push(`
            <div class="row">
              <i style="background: #${color}"></i>${labels[index]}
            </div>
          `);
      });
      div.innerHTML = rows.join("");
      return div;
    };

    legend.addTo(map);
  }, [map]);

  return null;
};

function customMarkerIcon(color) {
  const svgTemplate = `
    <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 32 32" class="marker">
      <path fill-opacity=".25" d="M16 32s1.427-9.585 3.761-12.025c4.595-4.805 8.685-.99 8.685-.99s4.044 3.964-.526 8.743C25.514 30.245 16 32 16 32z"/>
      <path fill="#${color}" stroke="#fff" d="M15.938 32S6 17.938 6 11.938C6 .125 15.938 0 15.938 0S26 .125 26 11.875C26 18.062 15.938 32 15.938 32zM16 6a4 4 0 100 8 4 4 0 000-8z"/>
    </svg>`;

  return new L.DivIcon({
    className: "test",
    html: svgTemplate,
    iconSize: [40, 40],
    iconAnchor: [12, 24],
    popupAnchor: [7, -16],
  });
}

const MyMarkers = ({ data }) => {
  const [selectedPoint, setSelectedPoint] = useState(null);

  const handleMarkerClick = (point) => {
    setSelectedPoint(point);
  };

  return data.map((item, index) => (
    <Marker
      key={index}
      icon={customMarkerIcon(colors[index])}
      position={{ lat: item.lat, lng: item.lng }}
      eventHandlers={{
        click: () => handleMarkerClick(item),
      }}
    >
      {selectedPoint === item && (
        <Popup>
          <div>
            <strong>{item.title}</strong>
            <Carousel>
              {item.images.map((image, index) => (
                <div key={index}>
                  <img src={image} alt={`Image ${index}`} />
                </div>
              ))}
            </Carousel>
            <p>Kat Sayısı: {item.propertyDetails.floors}</p>
            <p>Daire Sayısı: {item.propertyDetails.numberOfApartment}</p>
            <p>Durum: {item.propertyDetails.status}</p>
            <p>Sorumlu: {item.propertyDetails.responsible}</p>
            <p>Proje Detayları:</p>
            <ul>
              <li>Tamamlanma Tarihi: {item.propertyDetails.projectDetails.completionDate}</li>
              <li>Bütçe: {item.propertyDetails.projectDetails.budget}</li>
              <li>Mimar: {item.propertyDetails.projectDetails.architect}</li>
            </ul>
          </div>
        </Popup>
      )}
    </Marker>
  ));
};

const MapWrapper = () => {
  const [map, setMap] = useState(null);
  return (
    <MapContainer
      whenCreated={setMap}
      center={center}
      zoom={13}
      scrollWheelZoom={false}
    >
      <TileLayer {...tileLayer} />

      <MyMarkers data={points} />

      <Legend map={map} />
    </MapContainer>
  );
};

export default MapWrapper;
