import React, { useEffect, useState } from "react"; 
import { MapContainer, TileLayer, Marker, Popup, Polygon, LayersControl } from "react-leaflet";
import L from "leaflet";
import tileLayer from "../util/tileLayer";

const { BaseLayer, Overlay } = LayersControl;

const center = [41.0053, 28.9770]; // İstanbul Havalimanı koordinatları
const defaultZoom = 10;

// İkonları buradan değiştirebilirsiniz veya farklı ikonlar ekleyebilirsiniz
const iconTypes = {
  "Ofis": "office-icon",
  "Arsa": "land-icon", 
};

const createIcon = (iconType) => {
  return L.icon({
    iconUrl: require(`../assets/${iconType}.png`).default,
    iconSize: [30, 30],
    iconAnchor: [15, 30],
    popupAnchor: [0, -30],
  });
};

const MyMarkers = ({ data }) => {
  return (
    <>
      {data.map((item, index) => (
        <Marker
          key={index}
          position={[parseFloat(item.city_latitude), parseFloat(item.city_longitude)]}
          icon={createIcon(iconTypes[item.location_type_title])}
        >
          <Popup>
            <div className="popup-content">
              <p><strong>Mahal Kodu:</strong> {item.location_code}</p>
              <p><strong>Mahal İsmi:</strong> {item.location_title}</p>
              {/* Diğer bilgiler buraya eklenebilir */}
            </div>
          </Popup>
        </Marker>
      ))}
    </>
  );
};

const MapWrapper = () => {
  const [map, setMap] = useState(null);
  const [points, setPoints] = useState([]);

  // Örnek veri
  const sampleData = [
    {
      "city_latitude": "41.0053",
      "city_longitude": "28.9770",
      "location_code": "1303A",
      "location_title": "Ofis",
      "location_type_title": "Ofis"
    },
    {
      "city_latitude": "41.0053",
      "city_longitude": "28.9770",
      "location_code": "DL 529B",
      "location_title": "Arsa",
      "location_type_title": "Arsa"
    },
    // Diğer örnek veriler buraya eklenebilir
  ];

  useEffect(() => {
    setPoints(sampleData);
  }, []);

  const handleViewportChanged = (viewport) => {
    const bounds = map.getBounds();
    const NE = bounds.getNorthEast();
    const SW = bounds.getSouthWest();

    // Burada haritada görünen bölgenin koordinatlarını kullanarak
    // bu bölge için verileri getirebilir ve setPoints ile güncelleyebilirsiniz
    console.log("Kuzeydoğu Koordinatları:", NE);
    console.log("Güneybatı Koordinatları:", SW);
  };

  return (
    <MapContainer
      whenCreated={setMap}
      center={center}
      zoom={defaultZoom}
      scrollWheelZoom={true} 
      onViewportChanged={handleViewportChanged}
    >
      <LayersControl position="topright">
        <BaseLayer checked name="OpenStreetMap">
          <TileLayer {...tileLayer} />
        </BaseLayer>
        {/* İsteğe bağlı olarak farklı harita katmanları eklenebilir */}
      </LayersControl>

      <MyMarkers data={points} />
    </MapContainer>
  );
};

export default MapWrapper;
