import React, { useState, useEffect } from "react"; 
import { MapContainer, TileLayer, Marker, Popup, LayersControl, Polygon } from "react-leaflet";
import tileLayer from "../util/tileLayer";
import "./MapForm.css"; // CSS dosyasını import ettik

const { BaseLayer, Overlay } = LayersControl;

const MapWrapper = () => {
  const [map, setMap] = useState(null);
  const [intersections, setIntersections] = useState([]);
  const [selectedIntersection, setSelectedIntersection] = useState(null);
  const [formData, setFormData] = useState({
    city: "",
    district: "",
    mahalleKodu: "",
    ucret: "",
    sorumlu: "",
    latitude: "",
    longitude: ""
  });
  const [districts, setDistricts] = useState([]);

  // Örnek veri
  const cities = [
    { name: "Ankara", districts: ["Çankaya", "Kızılay", "Sincan"] },
    { name: "İstanbul", districts: ["Beşiktaş", "Kadıköy", "Şişli"] },
    // Diğer şehirler buraya eklenebilir
  ];

  useEffect(() => {
    // Tarayıcının önbelleğinden verileri getir
    const cachedIntersections = JSON.parse(localStorage.getItem("intersections"));
    if (cachedIntersections) {
      setIntersections(cachedIntersections);
    }
  }, []);

  useEffect(() => {
    // Verileri tarayıcının önbelleğine kaydet
    localStorage.setItem("intersections", JSON.stringify(intersections));
  }, [intersections]);

  const handleViewportChanged = (viewport) => {
    // Harita bölgesi değiştiğinde burada işlemler yapabilirsiniz
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const newIntersection = { ...formData };
    setIntersections([...intersections, newIntersection]);
    setFormData({
      city: "",
      district: "",
      mahalleKodu: "",
      ucret: "",
      sorumlu: "",
      latitude: "",
      longitude: ""
    });
  };

  const handleCityChange = (e) => {
    const selectedCity = e.target.value;
    const selectedDistricts = cities.find(city => city.name === selectedCity).districts;
    setDistricts(selectedDistricts);
    setFormData({
      ...formData,
      city: selectedCity,
      district: selectedDistricts[0] // İlk ilçeyi seçili hale getir
    });
  };

  const createSquareBounds = (lat, lng) => {
    const halfSideLength = 0.0025; // Kare kenar uzunluğunun yarısı
    return [
      [lat + halfSideLength, lng - halfSideLength], // Sol üst köşe
      [lat + halfSideLength, lng + halfSideLength], // Sağ üst köşe
      [lat - halfSideLength, lng + halfSideLength], // Sağ alt köşe
      [lat - halfSideLength, lng - halfSideLength], // Sol alt köşe
    ];
  };

  return (
    <div className="map-container">
      <form className="intersection-form" onSubmit={handleSubmit}>
        <h2>Kavşak Ekle</h2>
        <label>
          Şehir:
          <select name="city" value={formData.city} onChange={handleCityChange}>
            <option value="">Şehir Seçiniz</option>
            {cities.map((city, index) => (
              <option key={index} value={city.name}>{city.name}</option>
            ))}
          </select>
        </label>
        <label>
          İlçe:
          <select name="district" value={formData.district} onChange={handleInputChange}>
            {districts.map((district, index) => (
              <option key={index} value={district}>{district}</option>
            ))}
          </select>
        </label>
        <label>
          Mahalle Kodu:
          <input type="text" name="mahalleKodu" value={formData.mahalleKodu} onChange={handleInputChange} />
        </label>
        <label>
          Ücret:
          <input type="text" name="ucret" value={formData.ucret} onChange={handleInputChange} />
        </label>
        <label>
          Sorumlu:
          <input type="text" name="sorumlu" value={formData.sorumlu} onChange={handleInputChange} />
        </label>
        <label>
          Latitude:
          <input type="text" name="latitude" value={formData.latitude} onChange={handleInputChange} />
        </label>
        <label>
          Longitude:
          <input type="text" name="longitude" value={formData.longitude} onChange={handleInputChange} />
        </label>
        <button type="submit">Kavşak Ekle</button>
      </form>

      <MapContainer
        whenCreated={setMap}
        center={[39.9334, 32.8597]} // Ankara koordinatları
        zoom={6}
        scrollWheelZoom={true} 
        onViewportChanged={handleViewportChanged}
      >
        <LayersControl position="topright">
          <BaseLayer checked name="OpenStreetMap">
            <TileLayer {...tileLayer} />
          </BaseLayer>

          <Overlay checked name="Kavşaklar">
            {intersections.map((intersection, index) => (
              <Marker
                key={index}
                position={[parseFloat(intersection.latitude), parseFloat(intersection.longitude)]}
                eventHandlers={{
                  click: () => setSelectedIntersection(intersection),
                }}
              >
                <Popup>
                  <div className="popup-content">
                    <p><strong>Kavşak Adı:</strong> {intersection.name}</p>
                    <p><strong>Kavşak Tipi:</strong> {intersection.type}</p>
                    {/* Diğer bilgiler buraya eklenebilir */}
                  </div>
                </Popup>
              </Marker>
            ))}
          </Overlay>
        </LayersControl>

        {selectedIntersection && (
          <Popup position={[selectedIntersection.latitude, selectedIntersection.longitude]} onClose={() => setSelectedIntersection(null)}>
            <div>
              <h3>{selectedIntersection.name}</h3>
              <p><strong>Kavşak Tipi:</strong> {selectedIntersection.type}</p>
              {/* Diğer bilgiler buraya eklenebilir */}
            </div>
          </Popup>
        )}

        {/* Alanı çizmek için kare oluştur */
        formData.latitude && formData.longitude && (
          <Polygon pathOptions={{ color: 'blue' }} positions={createSquareBounds(parseFloat(formData.latitude), parseFloat(formData.longitude))} />
        )}
      </MapContainer>
    </div>
  );
};

export default MapWrapper;
