import React, { useEffect, useState } from "react";
import { MapContainer, TileLayer, Marker, Popup, LayersControl, Polygon } from "react-leaflet";
import tileLayer from "../util/tileLayer";
import "./MapForm.css"; // CSS dosyasını import ettik

const { BaseLayer, Overlay } = LayersControl;

const center = [39.9334, 32.8597]; // Ankara koordinatları
const defaultZoom = 6;

const MyMarkers = ({ data, handleMarkerClick }) => {
  return (
    <>
      {data.map((item, index) => (
        <Marker
          key={index}
          position={[parseFloat(item.latitude), parseFloat(item.longitude)]}
          eventHandlers={{
            click: () => handleMarkerClick(item),
          }}
        >
          <Popup>
            <div className="popup-content">
              <p><strong>Mahal Kodu:</strong> {item.name}</p>
              <p><strong>İşletmeci:</strong> {item.type}</p>
              <p><strong>GGKP:</strong> {item.GGKP}</p>
              <p><strong>Aylık Kira:</strong> {item.aylıkKira}</p>
              <p><strong>Toplam Kira:</strong> {item.toplamKira}</p>
              <p><strong>Tarife Durumu:</strong> {item.tarifeDurumu}</p>
              {/* Diğer bilgiler buraya eklenebilir */}
            </div>
          </Popup>
        </Marker>
      ))}
    </>
  );
};

const MapWrapper = () => {
  const [map, setMap] = useState(null);
  const [neighborhoods, setNeighborhoods] = useState([]);
  const [selectedNeighborhood, setSelectedNeighborhood] = useState(null);
  const [formData, setFormData] = useState({
    city: "", // Varsayılan olarak boş seçili
    name: "",
    type: "",
    GGKP: "",
    aylıkKira: "",
    toplamKira: "",
    işletmeci: "",
    tarifeDurumu: ""
  });

  useEffect(() => {
    // Örnek veri
    const sampleNeighborhoods = [
      { city: "Ankara", name: "Ankara Mahalsi", type: "Mahal", latitude: 39.9334, longitude: 32.8597 },
      { city: "İstanbul", name: "İstanbul Mahalsi", type: "Mahal", latitude: 41.0082, longitude: 28.9784 },
      { city: "İzmir", name: "İzmir Mahalsi", type: "Mahal", latitude: 38.4192, longitude: 27.1287 },
      { city: "Bursa", name: "Bursa Mahalsi", type: "Mahal", latitude: 40.1820, longitude: 29.0678 },
      // Diğer örnek veriler buraya eklenebilir
      { city: "Antalya", name: "Antalya Mahalsi", type: "Mahal", latitude: 36.8969, longitude: 30.7133 },
      { city: "Adana", name: "Adana Mahalsi", type: "Mahal", latitude: 37.0017, longitude: 35.3289 }
    ];
    setNeighborhoods(sampleNeighborhoods);
  }, []);

  const handleViewportChanged = (viewport) => {
    const bounds = map.getBounds();
    const NE = bounds.getNorthEast();
    const SW = bounds.getSouthWest();

    // Burada haritada görünen bölgenin koordinatlarını kullanarak
    // bu bölge için verileri getirebilir ve setNeighborhoods ile güncelleyebilirsiniz
    console.log("Kuzeydoğu Koordinatları:", NE);
    console.log("Güneybatı Koordinatları:", SW);
  };

  const handleMarkerClick = (neighborhood) => {
    setSelectedNeighborhood(neighborhood);
  };

  const createSquareBounds = (lat, lng) => {
    const halfSideLength = 0.0025; // Kare kenar uzunluğunun yarısı
    return [
      [lat + halfSideLength, lng - halfSideLength], // Sol üst köşe
      [lat + halfSideLength, lng + halfSideLength], // Sağ üst köşe
      [lat - halfSideLength, lng + halfSideLength], // Sağ alt köşe
      [lat - halfSideLength, lng - halfSideLength], // Sol alt köşe
    ];
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const selectedCityData = neighborhoods.find(item => item.city === formData.city);
    if (selectedCityData) {
      const newNeighborhood = { 
        city: selectedCityData.city, 
        name: formData.name, 
        type: formData.type, 
        latitude: selectedCityData.latitude, 
        longitude: selectedCityData.longitude,
        GGKP: formData.GGKP,
        aylıkKira: formData.aylıkKira,
        toplamKira: formData.toplamKira,
        işletmeci: formData.işletmeci,
        tarifeDurumu: formData.tarifeDurumu
      };
      setNeighborhoods([...neighborhoods, newNeighborhood]);
    }
  };

  const handleShowOnMap = () => {
    const selectedCityData = neighborhoods.find(item => item.city === formData.city);
    if (selectedCityData) {
      const newNeighborhood = { 
        city: selectedCityData.city, 
        name: formData.name, 
        type: formData.type, 
        latitude: selectedCityData.latitude, 
        longitude: selectedCityData.longitude,
        GGKP: formData.GGKP,
        aylıkKira: formData.aylıkKira,
        toplamKira: formData.toplamKira,
        işletmeci: formData.işletmeci,
        tarifeDurumu: formData.tarifeDurumu
      };
      setNeighborhoods([...neighborhoods, newNeighborhood]);
    }
  };

  return (
    <div>
      {/* Form */}
      <form onSubmit={handleSubmit} className="form">
        <label>
          Şehir:
          <select name="city" value={formData.city}          onChange={(e) => setFormData({ ...formData, city: e.target.value })}>
            <option value="">Şehir Seçin</option>
            {neighborhoods.map((neighborhood, index) => (
              <option key={index} value={neighborhood.city}>{neighborhood.city}</option>
            ))}
          </select>
        </label>
        <label>
          Mahal Kodu:
          <input type="text" name="name" value={formData.name} onChange={(e) => setFormData({ ...formData, name: e.target.value })} />
        </label>
        <label>
          İşletmeci:
          <input type="text" name="type" value={formData.type} onChange={(e) => setFormData({ ...formData, type: e.target.value })} />
        </label>
        <label>
          GGKP:
          <input type="text" name="GGKP" value={formData.GGKP} onChange={(e) => setFormData({ ...formData, GGKP: e.target.value })} />
        </label>
        <label>
          Aylık Kira:
          <input type="text" name="aylıkKira" value={formData.aylıkKira} onChange={(e) => setFormData({ ...formData, aylıkKira: e.target.value })} />
        </label>
        <label>
          Toplam Kira:
          <input type="text" name="toplamKira" value={formData.toplamKira} onChange={(e) => setFormData({ ...formData, toplamKira: e.target.value })} />
        </label>
        <label>
          İşletmeci:
          <select
          name="işletmeci"
          value={formData.işletmeci}
          onChange={(e) => setFormData({ ...formData, işletmeci: e.target.value })}
        >
          <option value="">İşletmeci Seçin</option>
          <option value="DHMI">DHMI</option>
          <option value="IGA">IGA</option>
          <option value="FRAPORT TAV">FRAPORT TAV</option>
          <option value="ISG SABIHA GOKÇEN">ISG SABIHA GOKÇEN</option>
        </select>
        </label>
        <label>
          Tarife Durumu:
          <input type="text" name="tarifeDurumu" value={formData.tarifeDurumu} onChange={(e) => setFormData({ ...formData, tarifeDurumu: e.target.value })} />
        </label>
        <button type="submit">Ekle</button>
        <button type="button" onClick={handleShowOnMap}>Haritada Göster</button>
      </form>

      <MapContainer
        whenCreated={setMap}
        center={center}
        zoom={defaultZoom}
        scrollWheelZoom={true} 
        onViewportChanged={handleViewportChanged}
      >
        <LayersControl position="topright">
          <BaseLayer checked name="OpenStreetMap">
            <TileLayer {...tileLayer} />
          </BaseLayer>
          {/* İsteğe bağlı olarak farklı harita katmanları eklenebilir */}

          <Overlay checked name="Mahaller">
            <MyMarkers data={neighborhoods} handleMarkerClick={handleMarkerClick} />
          </Overlay>
        </LayersControl>

        {selectedNeighborhood && (
          <Popup position={[selectedNeighborhood.latitude, selectedNeighborhood.longitude]} onClose={() => setSelectedNeighborhood(null)}>
            <div>
              <h3>{selectedNeighborhood.name}</h3>
              <p><strong>Mahal Tipi:</strong> {selectedNeighborhood.type}</p>
              <p><strong>GGKP:</strong> {selectedNeighborhood.GGKP}</p>
              <p><strong>Aylık Kira:</strong> {selectedNeighborhood.aylıkKira}</p>
              <p><strong>Toplam Kira:</strong> {selectedNeighborhood.toplamKira}</p>
              <p><strong>İşletmeci:</strong> {selectedNeighborhood.işletmeci}</p>
              <p><strong>Tarife Durumu:</strong> {selectedNeighborhood.tarifeDurumu}</p>
              {/* Diğer bilgiler buraya eklenebilir */}
            </div>
          </Popup>
        )}

        {/* Kare içindeki noktaları çizmek için marker kullanımı */}
        {neighborhoods.map((neighborhood, index) => (
          <Polygon
            key={index}
            pathOptions={{ color: "green" }}
            positions={createSquareBounds(parseFloat(neighborhood.latitude), parseFloat(neighborhood.longitude))}
          />
        ))}
      </MapContainer>
    </div>
  );
};

export default MapWrapper;
